draw(O--B);
draw((1,0,0)..(1/sqrt(2),0,1/sqrt(2))..(0,0,1));
draw((1/sqrt(2),1/sqrt(2),0)..(1,0,0)..(1/sqrt(2),-1/sqrt(2),0),red);
draw((1/sqrt(2),1/sqrt(2),0)..(Sin(75)*Cos(45),Sin(75)*Sin(45),Cos(75))..B,red);
draw((1/sqrt(2),-1/sqrt(2),0)..(Sin(75)*Cos(45),-Sin(75)*Sin(45),Cos(75))..A,red);
draw(O--(1/sqrt(2),1/sqrt(2),0));
draw(O--(1/sqrt(2),-1/sqrt(2),0));
draw(A..(sqrt(15)/5,0,sqrt(10)/5)..B,red);
