H = orthocenter(A,B,C);
D = (A + reflect(B,C)*(A))/2;
E = (B + reflect(C,A)*(B))/2;
F = (C + reflect(A,B)*(C))/2;

draw(A--B--C--cycle);
draw(A--D,dashed);
draw(B--E,dashed);
draw(C--F,dashed);

